tuv.seg:
import xmltree
var g = newElement("myTag") g.add newText("some text") g.add newComment("this is comment")
var h = newElement("secondTag") h.add newEntity("some entity")
let att = {"key1": "first value", "key2": "second value"}.toXmlAttributes let k = newXmlTree("treeTag", [g, h], att)
echo k # <treeTag key2="second value" key1="first value"> # <myTag>some text<!-- this is comment --></myTag> # <secondTag>&some entity;</secondTag> # </treeTag>

import xmltree
var g = newElement("myTag") g.add newText("テキスト") g.add newComment("これはコメントです")
var h = newElement("secondTag") h.add newEntity("エンティティ")
let att = {"key1": "第一値", "key2": "第二値"}.toXmlAttributes let k = newXmlTree("treeTag", [g, h], att)
echo k # <treeTag key2="第二値" key1="第一値"> # <myTag>テキスト<!-- これはコメントです --></myTag> # <secondTag>&エンティティ;</secondTag> # </treeTag>

var g = newElement("myTag") g.add newText("some text") g.add newComment("this is comment")
var h = newElement("secondTag") h.add newEntity("some entity")
let att = {"key1": "first value", "key2": "second value"}.toXmlAttributes var k = newXmlTree("treeTag", [g, h], att)
echo k ## <treeTag key2="second value" key1="first value"> ## <myTag>some text<!-- this is comment --></myTag> ## <secondTag>&some entity;</secondTag> ## </treeTag>
clear(k) echo k ## <treeTag key2="second value" key1="first value" />

var g = newElement("myTag") g.add newText("テキスト") g.add newComment("これはコメントです")
var h = newElement("secondTag") h.add newEntity("エンティティ")
let att = {"key1": "第一値", "key2": "第二値"}.toXmlAttributes var k = newXmlTree("treeTag", [g, h], att)
echo k ## <treeTag key2="第二値" key1="第一値"> ## <myTag>テキスト<!-- これはコメントです --></myTag> ## <secondTag>&エンティティ;</secondTag> ## </treeTag>
clear(k) echo k ## <treeTag key2="第二値" key1="第一値" />

var g = newElement("myTag") g.add newText("some text") g.add newComment("this is comment")
var h = newElement("secondTag") h.add newEntity("some entity") g.add h
assert $g == "<myTag>some text<!-- this is comment --><secondTag>&some entity;</secondTag></myTag>" for x in g: # the same as `for x in items(g):`

var g = newElement("myTag") g.add newText("テキスト") g.add newComment("これはコメントです")
var h = newElement("secondTag") h.add newEntity("エンティティ") g.add h
assert $g == "<myTag>テキスト<!-- これはコメントです --><secondTag>&エンティティ;</secondTag></myTag>" for x in g: # `for x in items(g):` と同じです
